import React from 'react'
import { Box, Stack } from "@chakra-ui/react"
import Card from './Card'
import axios from "axios";


const Home = () => {


    const checkoutHandler = async (amount) => {

        const { data: { key } } = await axios.get("http://www.localhost:4000/api/getkey")

        const { data: { order } } = await axios.post("http://localhost:4000/api/checkout", {
            amount
        })

        const options = {
            key,
            amount: order.amount,
            currency: "INR",
            name: "pay",
            description: "razerpay",
            image: "https://wallpapers.com/images/featured-full/valorant-305kescxw5dpup7y.jpg",
            order_id: order.id,
            callback_url: "http://localhost:4000/api/paymentverification",
            prefill: {
                name: "nikki",
                email: "xyz@gmail.com",
                contact: "9999999999"
            },
            notes: {
                "address": "WVN"
            },
            theme: {
                "color": "#121212"
            }
        };
        const razor = new window.Razorpay(options);
        razor.open();
    }

    return (


                <Card amount={5000} img={"https://dotesports.com/wp-content/uploads/2023/07/All-weapon-skin-bundles-in-VALORANT.jpg"}  checkoutHandler={checkoutHandler} />



    )
}

export default Home
